def download_pdf(url): 
    url = url.strip()
    filename = data_path/fc.Path(url).name
    if filename.suffix != '.pdf': filename = filename.with_name(f"{filename.name}.pdf")
    if not filename.exists(): fc.urlretrieve(url, filename)
    return filename

